mapper.select:
select j.name as name, j.ID_DIRECTORY as directoryId, j.ID_TRANSFORMATION as transformationId, ifnull(usr.NAME,j.CREATED_USER) as createUser, j.CREATED_DATE as createDate, ifnull(usr_m.NAME,j.MODIFIED_USER) as modifiedUser, j.MODIFIED_DATE as modifiedDate from (SELECT * FROM r_transformation ORDER BY CREATED_DATE DESC) j left join r_user usr on j.CREATED_USER = usr.ID_USER left join r_user usr_m on j.MODIFIED_USER = usr_m.ID_USER where j.use_batchid='Y'
and j.`NAME` IN ( select TASK_NAME from d_task_group_member_attribute where TASK_GROUP_NAME in ( select TASK_GROUP_NAME from d_user_group_tasks_relation where USER_GROUP_NAME=#{2} ) AND TYPE='trans' )
limit #{0},#{1}

select count(1) from r_transformation j where j.use_batchid='Y'

    AND j.`NAME` IN
    (
        select DISTINCT TASK_NAME from d_task_group_member_attribute where TASK_GROUP_NAME in
        (
            select TASK_GROUP_NAME from
            d_user_group_tasks_relation where USER_GROUP_NAME=#{0}
        )
        AND TYPE='trans'
    )

select j.name as name, j.ID_DIRECTORY as directoryId, j.ID_TRANSFORMATION as transformationId, j.CREATED_USER as createUser, j.CREATED_DATE as createDate, j.MODIFIED_USER as modifiedUser, j.MODIFIED_DATE as modifiedDate from r_transformation j where j.use_batchid='Y'
AND j.`NAME` IN ( select TASK_NAME from d_task_group_member_attribute where TASK_GROUP_NAME in ( select TASK_GROUP_NAME from d_user_group_tasks_relation where USER_GROUP_NAME=#{4} ) AND TYPE='trans' )
and j.name LIKE concat('%',#{2},'%')
and j.CREATED_DATE > #{3}
limit #{0},#{1}

select count(1) from r_transformation j WHERE j.use_batchid='Y'

    AND j.`NAME` IN
    (
        select DISTINCT TASK_NAME from d_task_group_member_attribute where TASK_GROUP_NAME in
        (
            select TASK_GROUP_NAME from
            d_user_group_tasks_relation where USER_GROUP_NAME=#{2}
        )
     AND TYPE='trans'
    )


    and j.name LIKE concat('%',#{0},'%')


    and j.CREATED_DATE > #{1}
